(857, 'プレイプリーインターナショナル', '', 'プレイプリーインターナショナル', '', 23, 1041, '春岡通', 16, 1, '5-29-3', '464-0847', '', '名古屋市立春岡小学校', '名古屋市立若水中学校', '', '地下鉄吹上駅から徒歩10分', '', '', '応募受付中', '', '', 0, '初めての子でも楽しんで通える', 'プレイプリーインターナショナルは1～6歳を対象としたプリスクール。充実した教育カリキュラムをはじめ、野外学習、体操、ヨガなども取り入れられ、通園が初めての子でも楽しんで通えます。毎月第1日曜日には近隣地域の方を対象に英語図書教材の閲覧・貸し出しも行っています。', '', '', '2014年', '30人', '【保育時間】9：00～15：00', '', '', '', '', '', '', '', '', '', NULL, '', '', '052-761-3188', '', '', 'https://www.playpourri.com/', '', '', '', '', 0, ''),
(858, 'アライブインターナショナルプリスクール 千種校', '', 'アライブインターナショナルプリスクール 千種校', '', 23, 1041, '菊坂町', 22, 1, '1-7　パインエリゼ1F・2F', '464-0836', '', '名古屋市立田代小学校', '名古屋市立城山中学校', '', '地下鉄覚王山駅から徒歩5分', '', '', '応募受付中', '', '', 0, '英語で感性を育む質の高い教育カリキュラム', 'アライブインターナショナルプリスクール では、理科、社会、料理、アート、音楽、劇づくり、ヨガ、スポーツ、道徳など、英語で感性を育むための体験学習を取り入れた質の高い独自の教育カリキュラムを採用。少人数制クラスで、子どもたちは秘めた個性を大きく開花させています。日常生活に必要な基本的な知識だけでなく、幼少教育に必要な分野も毎月学習。年長生の後半には、小学校3年生程度の知識が身に付いています。', '', '', '2012年', '24人', '【保育時間】10：00～14：30', '日・祝祭日・年末年始', '', '', '', '', '', '', '', '', NULL, '', '', '052-757-5550', '', '', 'https://alive-co.com/', '', '', '', '', 0, ''),
